*/
var race = SerializerFactory.getSerializer('HL7V2').fromXML(msg['PID']['PID.10'].toString());
//logger.info("Race was " + race );

if (race == "2054-5^Black or African American^CDC")
	race = "2054-5^Black or African American^HL70005";
else if (race ==  "1002-5^American Indian or Alaska Native^CDC" || race == "2131-1^Native American Indian^CDC")
	race = "1002-5^American Indian or Alaska Native^HL70005";
else if (race ==  "2028-9^Asian^CDC")
	race = "2028-9^Asian^HL70005";
else if (race ==  "2076-8^Native Hawaiian or Other Pacific Islander^CDC" || race == "2131-1^Hawaiian^CDC" || race == "2131-1^Pacific Islander^CDC")
	race = "2076-8^Native Hawaiian or Other Pacific Islander^HL70005";
else if (race ==  "2131-1^Hispanic^CDC")
	race = "2131-1^Other Race^HL70005";
else if (race ==  "2106-3^White^CDC")
	race = "2106-3^White^HL70005";
else if (race ==  "DeclineToSpecify^Declined to specify^CDC")
	race = "";
else
	race = "2131-1^Other Race^HL70005";

msg['PID']['PID.10'] = race;
//logger.info("Race is now " + race);

